import React, {Component} from 'react';
import firebase from '../Firebase/firebase';

class viewStudent extends Component {
    state = {
        user: '',
        uid: '',

        studentToFind: '',

        StudentName: '',
        StudentNumber: '',
        CourseCode: '',
        CourseName: ''
    }

    findStudent = async(e) => {
        e.preventDefault()
        

        // get reference to database location we want
        const loc = firebase.database().ref('/students/' + _uid)
        loc.once('value', snapshot => {
            snapshot.forEach(child => {
                this.setState({
                    StudentName: child.val().studentName ,
                    CourseCode: child.val().courseCode ,
                    CourseName:  child.val().courseName ,
                })
            })
        })
    }

    componentDidMount = async () => {

        firebase.auth().onAuthStateChanged(function(user) {
            if (user) {
                // User is signed in.
                this.setState({user: firebase.auth().currentUser}) //not needed?
                this.setState({uid: firebase.auth().currentUser.uid })
            } else {
                // No user is signed in.
                //redirect to login
                this.props.history.push('/login')
            }
            }.bind(this))
            var _uid = this.state.uid
        }

    render() {
        return (
            <div align="center"className="container">
                <h1> View Student page </h1>
                
                <div className="form-group " style={{width: "40%"}}>
                    <input value={this.state.studentToFind} onChange={this.handleChange} className="form-control" id="studentToFind" type="text" name="studentToFind" placeholder="Student ID" required/> 
                </div>
            </div>
        )
    }
}

export default viewStudent;
